import React, { useState, useContext } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from './AuthContext'; // Import the AuthContext

const LoginForm = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState(''); // State to handle error messages
    const navigate = useNavigate();
    const { setAuthToken } = useContext(AuthContext); // Use the AuthContext

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.post('http://localhost:3000/login', { username, password });
            setAuthToken(response.data.token); // Save the token (adjust according to your API response)
            navigate('/dashboard'); // Redirect to dashboard
        } catch (err) {
            // Handle errors like wrong credentials, server issues, etc.
            setError(err.response ? err.response.data.message : 'Login failed');
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            {error && <p>{error}</p>} {/* Display error message */}
            <input type="text" value={username} onChange={(e) => setUsername(e.target.value)} placeholder="Username" />
            <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} placeholder="Password" />
            <button type="submit">Login</button>
        </form>
    );
};

export default LoginForm;
